require 'spec_helper'

describe Tapyrus::Message::Block do

  describe 'parse from payload' do
    # 000000000000a7c25a2032d97800c509e4d6ccd633212c5956a52c58d8cef11d block data
    subject { Tapyrus::Message::Block.parse_from_payload(''.htb) }
    it 'should be parsed' do
      expect(subject.header.block_hash).to eq('1df1ced8582ca556592c2133d6ccd6e409c50078d932205ac2a7000000000000')
      expect(subject.transactions.length).to eq(1)
      expect(subject.transactions.first.txid).to eq('71d91ef4e7aff57d5f1383f681d36d9bcdfde43b2b6bab44a2221cf33a43e202')
    end
  end

  describe 'to_pkt' do

    context 'not use segwit' do
      subject {
        h = Tapyrus::BlockHeader.parse_from_payload('00000020f29ae31fe472fea5a9812cd8bd9d73c7e4491ee62fbaf9b1be20000000000000e4e24580186a17432dee5ada29678f3f5e6b51a451f3b8d09917a2de11dba12d11bd48590bd6001bcd3c87cb'.htb)
        b = Tapyrus::Message::Block.new(h)
        tx = Tapyrus::Tx.parse_from_payload(''.htb)
        b.transactions << tx
        b.to_pkt
      }
      it 'should be generate' do
        expect(subject).to eq(''.htb)
      end
    end

    context 'use segwit' do
      subject {
        h = Tapyrus::BlockHeader.parse_from_payload('00000020f29ae31fe472fea5a9812cd8bd9d73c7e4491ee62fbaf9b1be20000000000000e4e24580186a17432dee5ada29678f3f5e6b51a451f3b8d09917a2de11dba12d11bd48590bd6001bcd3c87cb'.htb)
        b = Tapyrus::Message::Block.new(h)
        b.use_segwit = true
        tx = Tapyrus::Tx.parse_from_payload(''.htb)
        b.transactions << tx
        b.to_pkt
      }
      it 'should be generate' do
        expect(subject).to eq(''.htb)
      end
    end

  end

end